css:
.container {
      width: 100%;
      height: 60vh;
      display: flex;
      flex-direction: column;
     align-items: center;
      justify-content: center;

    }
    .print-format {

        padding-left: 0px;
    }

    .pos-right{
        position:relative;
        right:100px;
    }

    .pos-left{
        position:relative;
        left:100px;
    }

    table {
      width: 60%;
      border-collapse: collapse;
      margin-bottom: 20px;
    }

    .tdimg {
      padding: 10px;
      border: none;  /* Remove cell borders */
      text-align: center;  /* Align text horizontally in the center */
      vertical-align: middle;  /* Align text vertically in the middle */
    }

    .tdarabic {
      padding: 10px;
      border: none;  /* Remove cell borders */
      text-align: right;  /* Align text horizontally in the center */
        /* Align text vertically in the middle */
    }

    img {
      display: block;
      max-width: 20%;
      height: auto;
      margin: 0 auto;
    }

    hr {
      width: 100%;
      margin-bottom: 20px;
    }
    .font {
      font-size: x-large;
    }
    .largefont{
      font-size: large;
    }
    .title-td{
      font-size: x-large;
      text-align: center;
      font-weight: bold;
       vertical-align: middle; 
    }
     .thank-td{
      font-size: large;
      text-align: center;
      font-weight: bold;
       vertical-align: middle; 
    }
    .dep-td{
      font-size: large;
      text-align: left;
      font-weight: bold;
       vertical-align: middle; 
    }
    .resized-td {
    width: 200px;
    height: 100px;
  }
